DROP TABLE IF EXISTS `tb_user`;
CREATE TABLE `tb_user` (
  `us_id` int(11) NOT NULL AUTO_INCREMENT,
  `us_email` varchar(50) DEFAULT NULL,
  `us_password` varchar(50) DEFAULT NULL,
  `us_name` varchar(50) DEFAULT NULL,
  `us_sex` varchar(50) DEFAULT NULL,
  `ca_id` int(11) DEFAULT NULL,
  `ca_name` varchar(50) DEFAULT NULL,
  `us_phone` varchar(100) DEFAULT NULL,
  `us_picture` varchar(50) DEFAULT NULL,
  `us_introduction` text,
  `us_achievements` text,
  `spare` varchar(255) DEFAULT NULL,
  PRIMARY KEY (`us_id`)
) ENGINE=InnoDB AUTO_INCREMENT=31 DEFAULT CHARSET=utf8;

