INSERT INTO `tb_user` VALUES ('26', 'sa@121', '12', '21', null, '4', '管理员', '', '7879f327-99ec-43e5-a5be-f53d1cf184d2.jpg', '<p>；“</p><a target=\"_blank\" href=\"https://baike.baidu.com/item/%E6%B0%91%E4%B8%8D%E7%95%8F%E6%AD%BB%EF%BC%8C%E5%A5%88%E4%BD%95%E4%BB%A5%E6%AD%BB%E6%83%A7%E4%B9%8B\">民不畏死，奈何以死惧之</a><p>”。他的哲学思想和由他创立的</p><a target=\"_blank\" href=\"https://baike.baidu.com/item/%E9%81%93%E5%AE%B6%E5%AD%A6%E6%B4%BE\">道家学派</a><p>，不但对中国古代思想文化的发展作出了重要贡献，而且对中国2000多年来思想文化的发展产生了深远的影响。关于他的身份，还有人认为他是</p><a target=\"_blank\" href=\"https://baike.baidu.com/item/%E8%80%81%E8%8E%B1%E5%AD%90\">老莱子</a><p>，也是楚国人，跟</p><a target=\"_blank\" href=\"https://baike.baidu.com/item/%E5%AD%94%E5%AD%90\">孔子</a><p>同时，曾著书十五篇宣传道家之用。</p><sup>[18]</sup><a>&nbsp;</a><a href=\"https://baike.baidu.com/pic/%E8%80%81%E5%AD%90/5448/0/7ab514d124883898572c84ba?fr=lemma&ct=single\" target=\"_blank\"><img src=\"https://gss2.bdstatic.com/-fo3dSag_xI4khGkpoWK1HF6hhy/baike/s%3D250/sign=4f756153972bd40746c7d4f84b889e9c/377adab44aed2e73cd8579578701a18b87d6fa59.jpg\" alt=\"老子骑牛图立轴\"></a><p>老子骑牛图立轴</p><a target=\"_blank\" href=\"https://baike.baidu.com/item/%E8%80%81%E8%81%83\">老聃</a><p>长寿，一百零一岁</p><a target=\"_blank\" href=\"https://baike.baidu.com/item/%E4%BB%99%E9%80%9D\">仙逝</a><p>，邻里皆来吊唁。老人哭之，如哭其子；少者哭之，如哭其母。念老子</p><a target=\"_blank\" href=\"https://baike.baidu.com/item/%E9%A1%BA%E6%B0%91\">顺民</a><p>之性、随民之情、与世无争、柔慈待人的大德大恩，皆悲不自胜。老聃好友秦佚来吊唁，至老子灵旁，不跪不拜，拱手致意，哭号三声即止。待其欲转身回去时，邻人拦住问道：“汝非老子好友乎？”秦佚答道；“当然。”邻人道：“既为老子好友，如此薄情少礼，可乎？”</p><a target=\"_blank\" href=\"https://baike.baidu.com/item/%E7%A7%A6%E4%BD%9A\">秦佚</a><p>道；“有何不可？”邻人闻言，由怒转，大声责问道：“其理何在？”秦佚笑道：“吾友老聃有言，生亦不喜，死亦不悲。汝可闻乎？昔日老聃之生也，由无至有，聚气而成，顺时而来，合自然之理，有何喜哉？今日老聃之死也，由有归无，散气而灭，顺时而去，合自然之理也。有何悲哉？生而喜者，是以为不当喜而喜也；死而悲者，是以为不当悲而悲也，放生时贵生，死时怕死，皆是以己之意愿而强求生来、强求死去也，皆背自然之理而任己之情也。如安时而处顺，则哀乐不能入也。而背自然、违天理，合于道乎？不合于道，可为老聃好</p><p><br></p>', '<p>；“</p><a target=\"_blank\" href=\"https://baike.baidu.com/item/%E6%B0%91%E4%B8%8D%E7%95%8F%E6%AD%BB%EF%BC%8C%E5%A5%88%E4%BD%95%E4%BB%A5%E6%AD%BB%E6%83%A7%E4%B9%8B\">民不畏死，奈何以死惧之</a><p>”。他的哲学思想和由他创立的</p><a target=\"_blank\" href=\"https://baike.baidu.com/item/%E9%81%93%E5%AE%B6%E5%AD%A6%E6%B4%BE\">道家学派</a><p>，不但对中国古代思想文化的发展作出了重要贡献，而且对中国2000多年来思想文化的发展产生了深远的影响。关于他的身份，还有人认为他是</p><a target=\"_blank\" href=\"https://baike.baidu.com/item/%E8%80%81%E8%8E%B1%E5%AD%90\">老莱子</a><p>，也是楚国人，跟</p><a target=\"_blank\" href=\"https://baike.baidu.com/item/%E5%AD%94%E5%AD%90\">孔子</a><p>同时，曾著书十五篇宣传道家之用。</p><sup>[18]</sup><a>&nbsp;</a><a href=\"https://baike.baidu.com/pic/%E8%80%81%E5%AD%90/5448/0/7ab514d124883898572c84ba?fr=lemma&ct=single\" target=\"_blank\"><img src=\"https://gss2.bdstatic.com/-fo3dSag_xI4khGkpoWK1HF6hhy/baike/s%3D250/sign=4f756153972bd40746c7d4f84b889e9c/377adab44aed2e73cd8579578701a18b87d6fa59.jpg\" alt=\"老子骑牛图立轴\"></a><p>老子骑牛图立轴</p><a target=\"_blank\" href=\"https://baike.baidu.com/item/%E8%80%81%E8%81%83\">老聃</a><p>长寿，一百零一岁</p><a target=\"_blank\" href=\"https://baike.baidu.com/item/%E4%BB%99%E9%80%9D\">仙逝</a><p>，邻里皆来吊唁。老人哭之，如哭其子；少者哭之，如哭其母。念老子</p><a target=\"_blank\" href=\"https://baike.baidu.com/item/%E9%A1%BA%E6%B0%91\">顺民</a><p>之性、随民之情、与世无争、柔慈待人的大德大恩，皆悲不自胜。老聃好友秦佚来吊唁，至老子灵旁，不跪不拜，拱手致意，哭号三声即止。待其欲转身回去时，邻人拦住问道：“汝非老子好友乎？”秦佚答道；“当然。”邻人道：“既为老子好友，如此薄情少礼，可乎？”</p><a target=\"_blank\" href=\"https://baike.baidu.com/item/%E7%A7%A6%E4%BD%9A\">秦佚</a><p>道；“有何不可？”邻人闻言，由怒转，大声责问道：“其理何在？”秦佚笑道：“吾友老聃有言，生亦不喜，死亦不悲。汝可闻乎？昔日老聃之生也，由无至有，聚气而成，顺时而来，合自然之理，有何喜哉？今日老聃之死也，由有归无，散气而灭，顺时而去，合自然之理也。有何悲哉？生而喜者，是以为不当喜而喜也；死而悲者，是以为不当悲而悲也，放生时贵生，死时怕死，皆是以己之意愿而强求生来、强求死去也，皆背自然之理而任己之情也。如安时而处顺，则哀乐不能入也。而背自然、违天理，合于道乎？不合于道，可为老聃好</p><p><br></p>', null);
